//000000043200a:5:{i:0;a:8:{s:2:"Id";s:3:"350";s:12:"game_type_id";s:1:"2";s:7:"kind_id";s:2:"53";s:6:"status";s:1:"0";s:5:"ratio";s:2:"17";s:8:"question";s:21:"黑名单的作用？";s:8:"answer_g";s:373:"(1) 列入黑名单的玩家，会屏蔽他的邮件
